document.addEventListener("DOMContentLoaded",()=>{(async function(){const e=await fetch("/api/jobs"),n=await e.json(),t=document.getElementById("jobs");t&&(t.innerHTML="",n.forEach(e=>{const n=document.createElement("div");n.className="card",n.innerHTML=`<h3>${e.title}</h3><p>${e.description?e.description.substring(0,120):""}</p><a class="btn btn-primary" href="/job/${e._id}">Ver</a>`,t.appendChild(n)}))})().catch(console.error)});